CREATE TABLE AM_ASSET
(
    AM_ASSET_ID         VARCHAR2(14),
    INB_CATE_CD         VARCHAR2(10),
    CLASS_ID            VARCHAR2(20),
    ITEM_ID             VARCHAR2(10),
    ASSET_NO            VARCHAR2(14),
    SERIAL_NO           VARCHAR2(500),
    INCLUDE_RACK_YN     CHAR(1),
    USE_VERTICAL_YN     CHAR(1),
    LOCATION_ID         VARCHAR2(10),
    NEED_UNIT_QTY       NUMBER,
    ASSET_NM            VARCHAR2(500),
    ASSET_STAT_CD       VARCHAR2(10),
    OPENV_CATE_CD       VARCHAR2(10),
    INBOUND_DATE        VARCHAR2(8),
    OUTBOUND_DATE       VARCHAR2(8),
    INB_INST_DATE       VARCHAR2(8),
    WA_DUR_YY_CD        VARCHAR2(8),
    WA_START_DATE       VARCHAR2(8),
    WA_END_DATE         VARCHAR2(8),
    MA_START_DATE       VARCHAR2(8),
    MA_END_DATE         VARCHAR2(8),
    DUR_END_DATE        VARCHAR2(8),
    IDLE_DATE           VARCHAR2(8),
    DISUSE_DATE         VARCHAR2(8),
    LCHK_DATE           VARCHAR2(14),
    LCHK_USER_ID        VARCHAR2(10),
    LCHK_USER_NM        VARCHAR2(50),
    ASSET_USER_ID       VARCHAR2(10),
    ASSET_USER_NM       VARCHAR2(50),
    RACK_ASSET_ID       VARCHAR2(12),
    RACK_ASSET_NO       VARCHAR2(10),
    RACK_UNIT_NO        VARCHAR2(20),
    LOC_SURF_ID         VARCHAR2(10),
    SAP_SERIAL_NO       VARCHAR2(18),
    CNTR_ITEM_ID        VARCHAR2(16),
    CONTRACT_ID         VARCHAR2(16),
    CONTRACT_NO         VARCHAR2(16),
    CONTRACT_DATE       VARCHAR2(8),
    CONTRACT_NM         VARCHAR2(500),
    COMPANY_ID          VARCHAR2(10),
    ACQ_CORP_CD         VARCHAR2(10),
    ACQ_DEPT_ID         VARCHAR2(10),
    INSPECT_DATE        VARCHAR2(8),
    INSPECT_USER_ID     VARCHAR2(10),
    INSPECT_USER_NM     VARCHAR2(100),
    INSPECT_DEPT_NM     VARCHAR2(200),
    CONFIRM_USER_ID     VARCHAR2(10),
    CONFIRM_USER_NM     VARCHAR2(100),
    SAP_USEDEPT_NM      VARCHAR2(200),
    SAP_ACQ_DATE        VARCHAR2(8),
    SAP_ACQ_AMT         NUMBER(15) DEFAULT 0,
    SAP_DEPRE_AMT       NUMBER(15),
    SAP_ACCT_AMT        NUMBER(15),
    SAP_FIX_NO          VARCHAR2(15),
    SAP_ITEM_NO         VARCHAR2(10),
    ASSET_DESC          VARCHAR2(4000),
    ASSET_REG_USER_ID   VARCHAR2(10),
    ASSET_REG_DATE      VARCHAR2(14),
    UP_ASSET_ID         VARCHAR2(14),
    MNT_COMP_ID         VARCHAR2(16),
    MNT_RATIO           NUMBER(5,2),
    MNT_REMARK          VARCHAR2(4000),
    UNIT_END_NO         VARCHAR2(20),
    REG_DATETIME        VARCHAR2(14),
    REG_USER_ID         VARCHAR2(10),
    REG_USER_NM         VARCHAR2(100),
    REG_DEPT_NM         VARCHAR2(100),
    LMOD_DATETIME       VARCHAR2(14),
    LMOD_USER_ID        VARCHAR2(10),
    LMOD_USER_NM        VARCHAR2(100),
    LMOD_DEPT_NM        VARCHAR2(100),
    EOS_DATE            VARCHAR2(8),
    VENDOR_ID           VARCHAR2(8),
    VENDOR_NM           VARCHAR2(200)
)
TABLESPACE TS_INITSMD_01
STORAGE
(
    INITIAL 64K
    NEXT 1M
)
NOCOMPRESS;

CREATE UNIQUE INDEX AM_ASSET_PK
