mapper.insert:
INSERT INTO WF_BACH_OUT
    (
    NAME
    , BIGO
    , REG_USER_ID
    , REG_USER_NM
    , REG_STIME
    , KEY
    )
VALUES
    (
    #{NAME}
    , #{BIGO}
    , #{REG_USER_ID}
    , #{REG_USER_NM}
    , TO_CHAR(SYSDATE,'YYYY-MM-DD HH24:MI:ss')
    , (SELECT NVL(MAX(KEY)+1,1) AS KEY FROM WF_BACH_OUT)
    )

mapper.update:

	   UPDATE WF_BACH_OUT SET 
            NAME = #{NAME}
            , BIGO = #{BIGO}
      WHERE 1=1
        AND KEY = #{KEY}
