mapper.delete:
	    /* FOMM0920 수신함에서 메모삭제 */
	        UPDATE FOMM_MEMO_RCV
        SET
            DEL_YN = 'Y'
            ,DEL_TIME = TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
        WHERE  JOB_REV_NO = #{JOB_REV_NO}
            AND MEMO_RCV_ID = #{MEMO_RCV_ID}
            AND RCV_USER_ID = #{RCV_USER_ID}
            AND DEL_YN = 'N'
