mapper.select:
SELECT AUTOSAVE_ID , USER_ID , PROCESS_ID , PROCESS_NM , TITLE_NM , PROCESS_TYPE , SAVE_DT , AUTOSAVE_CD , DEL_YN FROM FOMM_AUTOSAVE WHERE 1=1
AND TITLE_NM = #{TITLE_NM}
AND DEL_YN = #{DEL_YN}

mapper.insert:
        INSERT INTO FOMM_AUTOSAVE
		(
            AUTOSAVE_ID
		    , USER_ID
		    , PROCESS_ID
		    , PROCESS_NM
		    , TITLE_NM
		    , PROCESS_TYPE
		    , SAVE_DT
		    , AUTOSAVE_CD
		    , DEL_YN
		)VALUES(
            #{AUTOSAVE_ID}
            , #{USER_ID}
            , #{PROCESS_ID}
            , #{PROCESS_NM}
            , #{TITLE_NM}
            , 'SRM'
            , TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
            , 'RQST00'
            ,'N'
		)

mapper.delete:
UPDATE FOMM_AUTOSAVE
SET       DEL_YN    = 'Y'
WHERE AUTOSAVE_ID = #{AUTOSAVE_ID}
