mapper.select:
/* FOMM1200WDAO.selectCatNh */ select '' AS ROWFLAG, SR_CAT_ID, SR_CAT_TYPE, SR_CAT_NAME, SR_TYPE, AUTH_ID, USE_YN, BIGO, REG_USER_ID, REG_USER_NM, REG_STIME, LMOD_USER_ID, LMOD_USER_NM, SYSMODTIME from SR_CATEGORY_NH where 1 = 1
AND REPLACE(UPPER(SR_CAT_NAME),' ','') LIKE '%'||REPLACE(UPPER(#{SR_CAT_NAME}),' ','')||'%'
AND SR_CAT_TYPE = #{SR_CAT_TYPE}
AND USE_YN = #{USE_YN}
ORDER BY SR_CAT_TYPE, SR_CAT_ID

/* FOMM1200WDAO.chkDupCatNh */
select count(*) RST_CNT
   from SR_CATEGORY_NH SM
where 1 = 1    
   AND SR_CAT_ID = #{SR_CAT_ID}
