mapper.update:
UPDATE PJ_CHECKLIST SET
	  STAGE_CD			= #{STAGE_CD}
	, USE_YN			= #{USE_YN}
	, ACT_STIME			= #{ACT_STIME}
	, ACT_ETIME			= #{ACT_ETIME}

	, LMOD_SDATE		= TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
	, LMOD_USER_ID		= #{LMID}														--최종수정자ID
	, LMOD_USER_NM		= (SELECT USER_NM FROM FOMM_USER_MT WHERE USER_ID = #{LMID})	--최종수정자이름
	, LMOD_USER_DEPT	= (SELECT DEPT_NM FROM FOMM_USER_MT WHERE USER_ID = #{LMID})	--최종수정자부서이름
WHERE 1 = 1
AND CHECKLIST_ID = #{CHECKLIST_ID}

UPDATE PJ_CHECKITEM SET
	  CHECKITEM_CD		= #{CHECKITEM_CD} 
	, CHECKITEM_NM		= #{CHECKITEM_NM} 		 
	, ORD_NO			= #{ORD_NO}

	, LMOD_SDATE		= TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
	, LMOD_USER_ID		= #{LMID}														--최종수정자ID
	, LMOD_USER_NM		= (SELECT USER_NM FROM FOMM_USER_MT WHERE USER_ID = #{LMID})	--최종수정자이름
	, LMOD_USER_DEPT	= (SELECT DEPT_NM FROM FOMM_USER_MT WHERE USER_ID = #{LMID})	--최종수정자부서이름

WHERE 1 = 1
AND CHECKLIST_ID	= #{CHECKLIST_ID}
AND CHECKITEM_ID	= #{CHECKITEM_ID}

mapper.delete:
DELETE FROM PJ_CHECKLIST
WHERE 1 = 1
AND CHECKLIST_ID = #{CHECKLIST_ID}

DELETE FROM PJ_CHECKITEM
WHERE 1 = 1
AND CHECKLIST_ID = #{CHECKLIST_ID}

DELETE FROM PJ_CHECKITEM
WHERE 1 = 1
AND CHECKLIST_ID	= #{CHECKLIST_ID}
AND CHECKITEM_ID	= #{CHECKITEM_ID}
