mapper.update:
UPDATE PJ_QA_LIST SET
	  QA_LIST_NM		= #{QA_LIST_NM}
	, USE_YN			= #{USE_YN}
	, ACT_STIME			= #{ACT_STIME}
	, ACT_ETIME			= #{ACT_ETIME}

	, LMOD_SDATE		= TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
	, LMOD_USER_ID		= #{LMID}														--최종수정자ID
	, LMOD_USER_NM		= (SELECT USER_NM FROM FOMM_USER_MT WHERE USER_ID = #{LMID})	--최종수정자이름
	, LMOD_USER_DEPT	= (SELECT DEPT_NM FROM FOMM_USER_MT WHERE USER_ID = #{LMID})	--최종수정자부서이름
WHERE 1 = 1
AND QA_LIST_ID = #{QA_LIST_ID}

UPDATE PJ_QA_ITEM SET
	  QA_ITEM_NM		= #{QA_ITEM_NM}
	, QA_ITEM_BASIS		= #{QA_ITEM_BASIS}
	, QA_ITEM_TGT_VAL	= #{QA_ITEM_TGT_VAL}

	, LMOD_SDATE		= TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
	, LMOD_USER_ID		= #{LMID}														--최종수정자ID
	, LMOD_USER_NM		= (SELECT USER_NM FROM FOMM_USER_MT WHERE USER_ID = #{LMID})	--최종수정자이름
	, LMOD_USER_DEPT	= (SELECT DEPT_NM FROM FOMM_USER_MT WHERE USER_ID = #{LMID})	--최종수정자부서이름

WHERE 1 = 1
AND QA_LIST_ID	= #{QA_LIST_ID}
AND QA_ITEM_ID	= #{QA_ITEM_ID}

mapper.delete:
DELETE FROM PJ_QA_LIST
WHERE 1 = 1
AND QA_LIST_ID = #{QA_LIST_ID}

DELETE FROM PJ_QA_ITEM 
WHERE 1 = 1
AND QA_LIST_ID = #{QA_LIST_ID}

DELETE FROM PJ_QA_ITEM 
WHERE 1 = 1
AND QA_LIST_ID	= #{QA_LIST_ID}
AND QA_ITEM_ID	= #{QA_ITEM_ID}
