mapper.select:
SELECT '0' AS CHK , '' AS ROWFLAG , A.ISSUE_ID , A.ISSUE_NO , A.ISSUE_NAME , A.PJT_ID , B.PJT_NAME AS PJT_NAME , A.RISK_ID , C.RISK_NO , C.RISK_NAME , A.ISSUE_STATUS_CD , A.ISSUE_TYPE_CD , A.ISSUE_DESC , A.ISSUE_STIME , A.ISSUE_IMPACT_CD , A.ISSUE_PLAN , A.CPL_PLAN_TIME , A.CPL_ACT_TIME , A.CHG_USER_ID , A.CHG_USER_NM , A.CHG_USER_DEPT , A.REG_STIME , A.REG_USER_ID , A.REG_USER_NM , A.REG_USER_DEPT , A.SYSMODTIME , A.LMOD_USER_ID , A.LMOD_USER_NM , A.LMOD_USER_DEPT
FROM PJ_ISSUE A , PJ_PROJECT B , PJ_RISK C
WHERE 1 = 1 AND A.PJT_ID = B.PJT_ID AND A.RISK_ID = C.RISK_ID(+) AND A.ISSUE_ID = #{ISSUE_ID}

mapper.update:
		UPDATE PJ_ISSUE SET
			  ISSUE_NO			= #{ISSUE_NO}
            , ISSUE_NAME		= #{ISSUE_NAME}
            , RISK_ID			= #{RISK_ID}
            , ISSUE_STATUS_CD	= #{ISSUE_STATUS_CD}
            , ISSUE_TYPE_CD		= #{ISSUE_TYPE_CD}
            , ISSUE_DESC		= #{ISSUE_DESC}
            , ISSUE_STIME		= #{ISSUE_STIME}
            , ISSUE_IMPACT_CD	= #{ISSUE_IMPACT_CD}
            , ISSUE_PLAN		= #{ISSUE_PLAN}
            , CPL_PLAN_TIME		= #{CPL_PLAN_TIME}
            , CPL_ACT_TIME		= #{CPL_ACT_TIME}           
            , CHG_USER_ID		= #{CHG_USER_ID}
            , CHG_USER_NM		= (SELECT USER_NM FROM FOMM_USER_MT WHERE USER_ID = #{CHG_USER_ID})
            , CHG_USER_DEPT		= (SELECT DEPT_NM FROM FOMM_USER_MT WHERE USER_ID = #{CHG_USER_ID})

			, SYSMODTIME		= TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
			, LMOD_USER_ID		= #{LMID}														--최종수정자ID
			, LMOD_USER_NM		= (SELECT USER_NM FROM FOMM_USER_MT WHERE USER_ID = #{LMID})	--최종수정자이름
			, LMOD_USER_DEPT	= (SELECT DEPT_NM FROM FOMM_USER_MT WHERE USER_ID = #{LMID})	--최종수정자부서이름
		WHERE 1 = 1
		AND ISSUE_ID = #{ISSUE_ID}

		UPDATE PJ_ISSUE_ACTION SET
			  ACT_DESC			= #{ACT_DESC}
            , ACT_STATUS_CD		= #{ACT_STATUS_CD}
            , ACT_TIME			= #{ACT_TIME}
            , ACT_USER_ID		= #{ACT_USER_ID}
			, ACT_USER_NM		= (SELECT USER_NM FROM FOMM_USER_MT WHERE USER_ID = #{ACT_USER_ID}) 
			, ACT_USER_DEPT 	= (SELECT DEPT_NM FROM FOMM_USER_MT WHERE USER_ID = #{ACT_USER_ID})

			, SYSMODTIME		= TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
			, LMOD_USER_ID		= #{LMID}														--최종수정자ID
			, LMOD_USER_NM		= (SELECT USER_NM FROM FOMM_USER_MT WHERE USER_ID = #{LMID})	--최종수정자이름
			, LMOD_USER_DEPT	= (SELECT DEPT_NM FROM FOMM_USER_MT WHERE USER_ID = #{LMID})	--최종수정자부서이름

		WHERE 1 = 1
		AND ISSUE_ID	= #{ISSUE_ID}
		AND ACT_ID		= #{ACT_ID}

mapper.delete:
DELETE FROM PJ_ISSUE_ACTION
WHERE 1 = 1
AND ISSUE_ID	= #{ISSUE_ID}
AND ACT_ID		= #{ACT_ID}
