mapper.select:
SELECT '0' AS CHK , '' AS ROWFLAG , A.FUNC_ID , A.FUNC_NO , A.FUNC_NAME , A.FUNC_TYPE_CD , A.PJT_ID , A.BIZ_ID , A.FUNC_LV1 , A.FUNC_LV2 , A.REG_STIME , A.REG_USER_ID , A.REG_USER_NM , A.REG_USER_DEPT , A.LMOD_SDATE , A.LMOD_USER_ID , A.LMOD_USER_NM , A.LMOD_USER_DEPT , A.FUNC_DESC , A.REF_ENTITY , B.PJT_NAME AS PJT_NAME , C.PJT_ID AS P_PJT_ID , C.PJT_NAME AS P_PJT_NAME
FROM PJ_FUNCTION A , PJ_PROJECT B , PJ_PROJECT C
WHERE 1 = 1 AND A.PJT_ID = B.PJT_ID AND B.PARENT_ID = C.PJT_ID AND A.FUNC_ID = #{FUNC_ID}

mapper.update:
		UPDATE PJ_FUNCTION SET
			  FUNC_NO			= #{FUNC_NO}
            , FUNC_NAME			= #{FUNC_NAME}
            , FUNC_TYPE_CD		= #{FUNC_TYPE_CD}

            , BIZ_ID			= #{BIZ_ID}
            , FUNC_LV1			= #{FUNC_LV1}
            , FUNC_LV2			= #{FUNC_LV2}
            , FUNC_DESC			= #{FUNC_DESC}
            , REF_ENTITY		= #{REF_ENTITY}

			, LMOD_SDATE		= TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
			, LMOD_USER_ID		= #{LMID}														--최종수정자ID
			, LMOD_USER_NM		= (SELECT USER_NM FROM FOMM_USER_MT WHERE USER_ID = #{LMID})	--최종수정자이름
			, LMOD_USER_DEPT	= (SELECT DEPT_NM FROM FOMM_USER_MT WHERE USER_ID = #{LMID})	--최종수정자부서이름

		WHERE 1 = 1
		AND PJT_ID	= #{PJT_ID}
		AND FUNC_ID	= #{FUNC_ID}

mapper.insert:
INSERT INTO PJ_FUNC_REQ_MAP
(
	  FUNC_ID
	, REQ_ID
	, PJT_ID

	, REG_STIME
	, REG_USER_ID
	, REG_USER_NM
	, REG_USER_DEPT
)
VALUES
(
	  #{REF_ID}
	, #{REQ_ID}
	, #{PJT_ID}

	, TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
	, #{LMID}
	, (SELECT USER_NM FROM FOMM_USER_MT WHERE USER_ID = #{LMID})
	, (SELECT DEPT_NM FROM FOMM_USER_MT WHERE USER_ID = #{LMID})
)

mapper.delete:
DELETE
FROM PJ_FUNC_REQ_MAP
WHERE 1 = 1
AND FUNC_ID = #{REF_ID}
AND REQ_ID	= #{REQ_ID}
