mapper.select:
SELECT '0' AS CHK , '' AS ROWFLAG , A.FUNC_ID , A.FUNC_NO , A.FUNC_NAME , A.FUNC_TYPE_CD , A.PJT_ID , A.BIZ_ID , D.BIZ_NAME , A.FUNC_LV1 , A.FUNC_LV2 , A.REG_STIME , A.REG_USER_ID , A.REG_USER_NM , A.REG_USER_DEPT , A.LMOD_SDATE , A.LMOD_USER_ID , A.LMOD_USER_NM , A.LMOD_USER_DEPT , A.FUNC_DESC , A.REF_ENTITY , B.PJT_NAME AS PJT_NAME , C.PJT_ID AS P_PJT_ID , C.PJT_NAME AS P_PJT_NAME
FROM PJ_FUNCTION A , PJ_PROJECT B , PJ_PROJECT C , PJ_BIZ D
WHERE 1 = 1 AND A.PJT_ID = B.PJT_ID AND B.PARENT_ID = C.PJT_ID AND A.PJT_ID = D.PJT_ID AND A.BIZ_ID = D.BIZ_ID AND A.PJT_ID = #{SUB_PJT_ID}
AND A.BIZ_ID = #{BIZ_ID}
AND UPPER(A.FUNC_NO) LIKE '%' || UPPER(#{FUNC_NO}) || '%'
AND UPPER(A.FUNC_LV1) LIKE '%' || UPPER(#{FUNC_LV1}) || '%'
AND UPPER(A.FUNC_LV2) LIKE '%' || UPPER(#{FUNC_LV2}) || '%'
AND UPPER(A.FUNC_NAME) LIKE '%' || UPPER(#{FUNC_NM}) || '%'
AND A.FUNC_TYPE_CD = #{FUNC_TYPE_CD}
ORDER BY A.FUNC_NO, A.FUNC_ID
