mapper.select:
SELECT '0' AS CHK , '' AS ROWFLAG , A.SCR_ID , A.SCR_NO , A.SCR_NAME , A.SCR_TYPE_CD , A.PJT_ID , A.BIZ_ID , D.BIZ_NAME , A.IMPORTANT_CD , A.DIFFICULT_CD , A.PLAN_SDATE , A.PLAN_EDATE , A.ACTL_SDATE , A.ACTL_EDATE , A.CHG_USER_NM , A.DEV_USER_ID , A.DEV_USER_NM , A.DEV_USER_DEPT , A.PL_USER_ID , A.PL_USER_NM , A.PL_USER_DEPT , A.REG_STIME , A.REG_USER_ID , A.REG_USER_NM , A.REG_USER_DEPT , A.LMOD_SDATE , A.LMOD_USER_ID , A.LMOD_USER_NM , A.LMOD_USER_DEPT , A.SCR_DESC , A.REMARK , B.PJT_NAME AS PJT_NAME , C.PJT_ID AS P_PJT_ID , C.PJT_NAME AS P_PJT_NAME
FROM PJ_SCREEN A , PJ_PROJECT B , PJ_PROJECT C , PJ_BIZ D
WHERE 1 = 1 AND A.PJT_ID = B.PJT_ID AND B.PARENT_ID = C.PJT_ID AND A.PJT_ID = D.PJT_ID AND A.BIZ_ID = D.BIZ_ID AND A.PJT_ID = #{SUB_PJT_ID}
AND A.BIZ_ID = #{BIZ_ID}
AND UPPER(A.SCR_NO) LIKE '%' || UPPER(#{SCR_NO}) || '%'
AND UPPER(A.SCR_NAME) LIKE '%' || UPPER(#{SCR_NM}) || '%'
AND A.SCR_TYPE_CD = #{SCR_TYPE_CD}
ORDER BY A.SCR_NO, A.SCR_ID
