mapper.select:
SELECT '0' AS CHK , A.INF_ID , A.INF_NO , A.INF_NAME , A.INF_TYPE_CD , A.PJT_ID , A.BIZ_ID , D.BIZ_NAME , A.REQ_ORG , A.REQ_BIZ , A.RSP_ORG , A.RSP_BIZ , A.INF_CLASS_CD , A.INF_PERIOD_CD , A.INF_METHOD_CD , A.INF_COM_TYPE , A.INF_TR_NO , A.INF_DESC , A.REMARK , A.PLAN_SDATE , A.PLAN_EDATE , A.ACTL_SDATE , A.ACTL_EDATE , A.CHG_USER_NM , A.DEV_USER_ID , A.DEV_USER_NM , A.DEV_USER_DEPT , A.PL_USER_ID , A.PL_USER_NM , A.PL_USER_DEPT , A.REG_STIME , A.REG_USER_ID , A.REG_USER_NM , A.REG_USER_DEPT , A.LMOD_SDATE , A.LMOD_USER_ID , A.LMOD_USER_NM , A.LMOD_USER_DEPT , B.PJT_NAME AS PJT_NAME , C.PJT_ID AS P_PJT_ID , C.PJT_NAME AS P_PJT_NAME
FROM PJ_INTERFACE A , PJ_PROJECT B , PJ_PROJECT C , PJ_BIZ D
WHERE 1 = 1 AND A.PJT_ID = B.PJT_ID AND B.PARENT_ID = C.PJT_ID AND A.PJT_ID = D.PJT_ID AND A.BIZ_ID = D.BIZ_ID AND A.PJT_ID = #{SUB_PJT_ID}
AND A.BIZ_ID = #{BIZ_ID}
AND UPPER(A.INF_NO) LIKE '%' || UPPER(#{INF_NO}) || '%'
AND UPPER(A.INF_NAME) LIKE '%' || UPPER(#{INF_NM}) || '%'
AND A.INF_TYPE_CD = #{INF_TYPE_CD}
ORDER BY A.INF_ID, A.INF_NO
