mapper.select:
SELECT '0' AS CHK , '' AS ROWFLAG , A.BTJB_ID , A.BTJB_NO , A.BTJB_NAME , A.BTJB_TYPE_CD , A.PJT_ID , A.BIZ_ID , A.PRE_BTJB_NO , A.POST_BTJB_NO , A.REG_STIME , A.REG_USER_ID , A.REG_USER_NM , A.REG_USER_DEPT , A.LMOD_SDATE , A.LMOD_USER_ID , A.LMOD_USER_NM , A.LMOD_USER_DEPT , A.BTJB_DESC , A.REMARK , B.PJT_NAME AS PJT_NAME , C.PJT_ID AS P_PJT_ID , C.PJT_NAME AS P_PJT_NAME
FROM PJ_BATCHJOB A , PJ_PROJECT B , PJ_PROJECT C
WHERE 1 = 1 AND A.PJT_ID = B.PJT_ID AND B.PARENT_ID = C.PJT_ID AND A.BTJB_ID = #{BTJB_ID}

mapper.update:
		UPDATE PJ_BATCHJOB SET
			  BTJB_NO			= #{BTJB_NO}
            , BTJB_NAME			= #{BTJB_NAME}
            , BTJB_TYPE_CD		= #{BTJB_TYPE_CD}
            , BIZ_ID			= #{BIZ_ID}
            , PRE_BTJB_NO		= #{PRE_BTJB_NO}
            , POST_BTJB_NO		= #{POST_BTJB_NO}
            , BTJB_DESC			= #{BTJB_DESC}
            , REMARK			= #{REMARK}

            , LMOD_SDATE		= TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
			, LMOD_USER_ID		= #{LMID}														--최종수정자ID
			, LMOD_USER_NM		= (SELECT USER_NM FROM FOMM_USER_MT WHERE USER_ID = #{LMID})	--최종수정자이름
			, LMOD_USER_DEPT	= (SELECT DEPT_NM FROM FOMM_USER_MT WHERE USER_ID = #{LMID})	--최종수정자부서이름

		WHERE 1 = 1
		AND BTJB_ID	= #{BTJB_ID}

mapper.insert:
INSERT INTO PJ_BTJB_REQ_MAP
(  
	  BTJB_ID
	, REQ_ID
	, PJT_ID
	, REG_STIME
	, REG_USER_ID
	, REG_USER_NM
	, REG_USER_DEPT
)
VALUES
(
	  #{REF_ID}
	, #{REQ_ID}
	, #{PJT_ID}
	, TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
	, #{LMID}
	, (SELECT USER_NM FROM FOMM_USER_MT WHERE USER_ID = #{LMID})
	, (SELECT DEPT_NM FROM FOMM_USER_MT WHERE USER_ID = #{LMID})
)

mapper.delete:
DELETE
FROM PJ_BTJB_REQ_MAP
WHERE 1 = 1
AND BTJB_ID	= #{REF_ID}
AND REQ_ID	= #{REQ_ID}
