mapper.select:
SELECT '0' AS CHK , '' AS ROWFLAG , A.BTJB_ID , A.BTJB_NO , A.BTJB_NAME , A.BTJB_TYPE_CD , A.PJT_ID , A.BIZ_ID , D.BIZ_NAME , A.PRE_BTJB_NO , A.POST_BTJB_NO , A.REG_STIME , A.REG_USER_ID , A.REG_USER_NM , A.REG_USER_DEPT , A.LMOD_SDATE , A.LMOD_USER_ID , A.LMOD_USER_NM , A.LMOD_USER_DEPT , A.BTJB_DESC , A.REMARK , B.PJT_NAME AS PJT_NAME , C.PJT_ID AS P_PJT_ID , C.PJT_NAME AS P_PJT_NAME
FROM PJ_BATCHJOB A , PJ_PROJECT B , PJ_PROJECT C , PJ_BIZ D
WHERE 1 = 1 AND A.PJT_ID = B.PJT_ID AND B.PARENT_ID = C.PJT_ID AND A.PJT_ID = D.PJT_ID AND A.BIZ_ID = D.BIZ_ID AND A.PJT_ID = #{SUB_PJT_ID}
AND A.BIZ_ID = #{BIZ_ID}
AND UPPER(A.BTJB_NO) LIKE '%' || UPPER(#{BTJB_NO}) || '%'
AND UPPER(A.BTJB_NAME) LIKE '%' || UPPER(#{BTJB_NM}) || '%'
AND A.BTJB_TYPE_CD = #{BTJB_TYPE_CD}
ORDER BY A.BTJB_NO, A.BTJB_ID
