mapper.select:
SELECT '0' AS CHK , '' AS ROWFLAG , A.PRG_ID , A.PRG_NO , A.PRG_NAME , A.PRG_TYPE_CD , A.PJT_ID , A.BIZ_ID , A.PLAN_SDATE , A.PLAN_EDATE , A.ACTL_SDATE , A.ACTL_EDATE , A.CHG_USER_NM , A.DEV_USER_ID , A.DEV_USER_NM , A.DEV_USER_DEPT , A.PL_USER_ID , A.PL_USER_NM , A.PL_USER_DEPT , A.REG_STIME , A.REG_USER_ID , A.REG_USER_NM , A.REG_USER_DEPT , A.LMOD_SDATE , A.LMOD_USER_ID , A.LMOD_USER_NM , A.LMOD_USER_DEPT , A.PRG_DESC , A.REMARK , B.PJT_NAME AS PJT_NAME , C.PJT_ID AS P_PJT_ID , C.PJT_NAME AS P_PJT_NAME
FROM PJ_PROGRAM A , PJ_PROJECT B , PJ_PROJECT C
WHERE 1 = 1 AND A.PJT_ID = B.PJT_ID AND B.PARENT_ID = C.PJT_ID AND A.PRG_ID = #{PRG_ID}

mapper.insert:

			SELECT
				'PJPR' || TO_CHAR(SYSDATE, 'YYMMDD') ||
				LPAD( NVL(MAX(TO_NUMBER(NVL(SUBSTR(PRG_ID, -6), 0) )), 0) + 1, 6, 0)
   			FROM PJ_PROGRAM
   			WHERE 1 = 1
   			AND PRG_ID LIKE 'PJPR%'
			AND LENGTH(PRG_ID) = 16


		INSERT INTO PJ_PROGRAM
		(  
			  PRG_ID
			, PRG_NO
			, PRG_NAME
			, PRG_TYPE_CD
			, PJT_ID
			, BIZ_ID
			, PLAN_SDATE
			, PLAN_EDATE
			, ACTL_SDATE
			, ACTL_EDATE
			, CHG_USER_NM
			, DEV_USER_ID
			, DEV_USER_NM
			, DEV_USER_DEPT
			, PL_USER_ID
			, PL_USER_NM
			, PL_USER_DEPT
			, PRG_DESC
			, REMARK

			, REG_STIME
			, REG_USER_ID
			, REG_USER_NM
			, REG_USER_DEPT

			, LMOD_SDATE
			, LMOD_USER_ID
			, LMOD_USER_NM
			, LMOD_USER_DEPT
		)
		VALUES
		(
			  #{PRG_ID}
			, #{PRG_NO}
			, #{PRG_NAME}
			, #{PRG_TYPE_CD}
			, #{PJT_ID}
			, #{BIZ_ID}
			, #{PLAN_SDATE}
			, #{PLAN_EDATE}
			, #{ACTL_SDATE}
			, #{ACTL_EDATE}
			, #{CHG_USER_NM}
			, #{DEV_USER_ID}
			, #{DEV_USER_NM}
			, #{DEV_USER_DEPT}
			, #{PL_USER_ID}
			, #{PL_USER_NM}
			, #{PL_USER_DEPT}
			, #{PRG_DESC}
			, #{REMARK}

			, TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
			, #{LMID}
			, (SELECT USER_NM FROM FOMM_USER_MT WHERE USER_ID = #{LMID})
			, (SELECT DEPT_NM FROM FOMM_USER_MT WHERE USER_ID = #{LMID})

			, TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
			, #{LMID}
			, (SELECT USER_NM FROM FOMM_USER_MT WHERE USER_ID = #{LMID})
			, (SELECT DEPT_NM FROM FOMM_USER_MT WHERE USER_ID = #{LMID})
		)

mapper.update:
		UPDATE PJ_PROGRAM SET
			  PRG_NO			= #{PRG_NO}
            , PRG_NAME			= #{PRG_NAME}
            , PRG_TYPE_CD		= #{PRG_TYPE_CD}
            , BIZ_ID			= #{BIZ_ID}
            , PLAN_SDATE		= #{PLAN_SDATE}
            , PLAN_EDATE		= #{PLAN_EDATE}
            , ACTL_SDATE		= #{ACTL_SDATE}
            , ACTL_EDATE		= #{ACTL_EDATE}
            , CHG_USER_NM		= #{CHG_USER_NM}
            , DEV_USER_ID		= #{DEV_USER_ID}
            , DEV_USER_NM		= #{DEV_USER_NM}
            , DEV_USER_DEPT		= #{DEV_USER_DEPT}
            , PL_USER_ID		= #{PL_USER_ID}
            , PL_USER_NM		= #{PL_USER_NM}
            , PL_USER_DEPT		= #{PL_USER_DEPT}
            , PRG_DESC			= #{PRG_DESC}
            , REMARK			= #{REMARK}

            , LMOD_SDATE		= TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
			, LMOD_USER_ID		= #{LMID}														--최종수정자ID
			, LMOD_USER_NM		= (SELECT USER_NM FROM FOMM_USER_MT WHERE USER_ID = #{LMID})	--최종수정자이름
			, LMOD_USER_DEPT	= (SELECT DEPT_NM FROM FOMM_USER_MT WHERE USER_ID = #{LMID})	--최종수정자부서이름

		WHERE 1 = 1
		AND PRG_ID	= #{PRG_ID}

mapper.insert:
INSERT INTO PJ_PRG_FUNC_MAP
(  
	  PRG_ID
	, FUNC_ID
	, PJT_ID

	, REG_STIME
	, REG_USER_ID
	, REG_USER_NM
	, REG_USER_DEPT
)
VALUES
(
	  #{REF_ID}
	, #{FUNC_ID}
	, #{PJT_ID}

	, TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
	, #{LMID}
	, (SELECT USER_NM FROM FOMM_USER_MT WHERE USER_ID = #{LMID})
	, (SELECT DEPT_NM FROM FOMM_USER_MT WHERE USER_ID = #{LMID})
)

mapper.delete:
DELETE
FROM PJ_PRG_FUNC_MAP
WHERE 1 = 1
AND PRG_ID	= #{REF_ID}
AND FUNC_ID	= #{FUNC_ID}
