mapper.select:
SELECT '0' AS CHK , A.STEST_ID , A.STEST_NO , A.STEST_TYPE_CD , A.STEST_VERSION , A.STEST_NAME , A.CASE_DESC , A.STEP_SEQ , A.STEP_NO , A.STEP_NAME , A.STEP_DESC , A.STEP_INPUT , A.STEP_EXPECT , A.PJT_ID , A.BIZ_ID , D.BIZ_NAME , A.RESULT_CD , A.REMARK , A.TESTER_ID , A.TESTER_NM , A.TESTER_DEPT , A.TESTER_PDATE , A.TESTER_ADATE , A.PL_USER_ID , A.PL_USER_NM , A.PL_USER_DEPT , A.PL_TEST_PDATE , A.PL_TEST_ADATE , A.UPMU_USER_ID , A.UPMU_USER_NM , A.UPMU_USER_DEPT , A.UPMU_TEST_PDATE , A.UPMU_TEST_ADATE , A.REG_STIME , A.REG_USER_ID , A.REG_USER_NM , A.REG_USER_DEPT , A.LMOD_SDATE , A.LMOD_USER_ID , A.LMOD_USER_NM , A.LMOD_USER_DEPT , B.PJT_NAME AS PJT_NAME , C.PJT_ID AS P_PJT_ID , C.PJT_NAME AS P_PJT_NAME
FROM PJ_SCNR_TEST A , PJ_PROJECT B , PJ_PROJECT C , PJ_BIZ D
WHERE 1 = 1 AND A.PJT_ID = B.PJT_ID AND B.PARENT_ID = C.PJT_ID AND A.PJT_ID = D.PJT_ID AND A.BIZ_ID = D.BIZ_ID AND A.PJT_ID = #{SUB_PJT_ID}
AND A.BIZ_ID = #{BIZ_ID}
AND UPPER(A.STEST_NO) LIKE '%' || UPPER(#{STEST_NO}) || '%'
AND UPPER(A.STEST_NAME) LIKE '%' || UPPER(#{STEST_NM}) || '%'
AND A.STEST_TYPE_CD = #{STEST_TYPE_CD}
AND A.STEST_VERSION = #{STEST_VERSION}
AND UPPER(A.STEP_NO) LIKE '%' || UPPER(#{STEP_NO}) || '%'
AND UPPER(A.STEP_NAME) LIKE '%' || UPPER(#{STEP_NM}) || '%'
AND A.RESULT_CD = #{RESULT_CD}
ORDER BY A.STEST_VERSION, A.STEST_NO, A.STEP_NO, A.STEST_ID
